import React from 'react';
import { View, StyleSheet, Text, TouchableOpacity, ScrollView } from "react-native";
import Carousel from 'react-native-snap-carousel';

export default function WarmUpExcercisesScreen() {

    const carouselItems = [
        {
            title:"Item 1",
            text: "Text 1",
        },
        {
            title:"Item 2",
            text: "Text 2",
        },
        {
            title:"Item 3",
            text: "Text 3",
        },
        {
            title:"Item 4",
            text: "Text 4",
        },
        {
            title:"Item 5",
            text: "Text 5",
        },
    ]

    return(
        <View>
            <Text>
                Разминка подготавливает ваше тело к физическим нагрузкам, чтобы предотвратить травмы. Ваша разминка должна длиться около 5 минут, и в конце разминки Вы должны почувствовать легкую одышку. Разминочные упражнения можно выполнять сидя или стоя. Если Вы выполняете разминку стоя, держитесь за устойчивую поверхность для поддержки, если это необходимо. 
                <Text style={styles.bold}>Повторите каждое движение 2-4 раза.</Text>
            </Text>

            <View style={{ flex: 1, flexDirection:'row', justifyContent: 'center', }}>
                <Carousel
                  layout={"default"}
                  data={carouselItems}
                  sliderWidth={300}
                  itemWidth={300}
                  renderItem={this._renderItem}
                  onSnapToItem = { index => this.setState({activeIndex:index}) } />
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    bold: {
        fontWeight: 'bold',
    },

    btn: {
        borderRadius: 25,
        width: 180,
        paddingTop: 18,
        paddingBottom: 18,
        paddingLeft: 10,
        paddingRight: 10,
        margin: 10,
        alignItems: 'center',
        textAlign: 'center',
        justifyContent: "center",
        backgroundColor: "skyblue",
    },
})
