import { React, useState } from 'react';
import { Text, View, SectionList, FlatList, TouchableOpacity, Button, StyleSheet } from 'react-native';

import { globalStyle } from  '../styles/style';

export default function ExcerciseTypesScreen({ navigation }) {

    //TODO: перенести в базу данных
    const [excercises, setExcercises] = useState([
        {
            name: 'Купирование отдышки',
            full: 'Здесь пока ничего нет!',
        },
        {
            name: 'Физические упражнения',
            full: 'Здесь пока ничего нет!',
        },
        {
            name: 'Восстановление голоса',
            full: 'Здесь пока ничего нет!',
        },
        {
            name: 'Восстановление функции глотания',
            full: 'Здесь пока ничего нет!',
        },
        {
            name: 'Восстановление памяти и внимания',
            full: 'Здесь пока ничего нет!',
        },
        {
            name: 'Преодоление стресса',
            full: 'Здесь пока ничего нет!',
        },
        {
            name: 'Выполнение повседневных функций',
            full: 'Здесь пока ничего нет!',
        },
    ]) 

    return (
        <View style={globalStyle.main}>
            {/* <View>
                <FlatList data={excercises} renderItem={({item}) => (
                    <TouchableOpacity style={styles.btn} onPress={() => navigation.navigate('Excercise', item)}>
                            <Text style={styles.text}>{item.name}</Text>   
                    </TouchableOpacity>
                )}/>
            </View> */}
            <Text>
            Физические упражнения являются важной частью восстановления здоровья после тяжелого заболевания, вызванного COVID-19. Физические упражнения могут помочь вам: 
                • Улучшить физическую форму 
                • Улучшить свое мышление 
                • Уменьшить симптомы одышки 
                • Увеличить мышечную силу 
                • Уменьшить стресс и улучшить настроение 
                • Улучшить чувство равновесия и координацию движений
                • Повысить чувство уверенности в себе 
                • Повысить уровень внутренней энергии
            </Text>
            <View class={styles.excs}>
                <TouchableOpacity style={styles.btn}>
                    <Text style={styles.text}>Разминочные упражнения</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.btn}>
                    <Text style={styles.text}>Оздоровительные физические упражнения</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.btn}>
                    <Text style={styles.text}>Укрепляющие (силовые) физические упражнения</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.btn}>
                    <Text style={styles.text}>Упражнения для охлаждения организма после тренировки</Text>
                </TouchableOpacity>
            </View>
      </View>
    )
}

const styles = StyleSheet.create({
    excs: {
        flex: 1,
        flexWrap: 1,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 2,
    },
    btn: {
        flexDirection: 'row',
        borderRadius: 25,
        width: '50%',
        height: 100,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 8,
        marginBottom: 8,
        backgroundColor: "skyblue",
    },
    text: {
        fontSize: 17,
        color: 'darkblue',
    },
})
