import React from 'react';
import { View, StyleSheet, Text, Image, TouchableOpacity, ScrollView, Item } from "react-native";

export default function WarmUpExcercisesScreen() {


    return(
        <View>
            <Text>
                Разминка подготавливает ваше тело к физическим нагрузкам, чтобы предотвратить травмы. Ваша разминка должна длиться около 5 минут, и в конце разминки Вы должны почувствовать легкую одышку. Разминочные упражнения можно выполнять сидя или стоя. Если Вы выполняете разминку стоя, держитесь за устойчивую поверхность для поддержки, если это необходимо. 
                <Text style={styles.bold}> Повторите каждое движение 2-4 раза.</Text>
            </Text>

            <ScrollView
                horizontal={true}
                contentContainerStyle={{ width: 100 }}
                showsHorizontalScrollIndicator={false}
                pagingEnabled
                >
                <View>
                    <Image 
                        source={require('../../../src/assets/')} 
                        />
                    <Text style={styles.bold}>Пожимание плечами:</Text>
                    <Text>Медленно поднимите плечи как можно выше вверх и снова опустите их.</Text>
                </View>
            </ScrollView>
        </View>
    )
}

const styles = StyleSheet.create({
    bold: {
        fontWeight: 'bold',
    },

    btn: {
        borderRadius: 25,
        width: 180,
        paddingTop: 18,
        paddingBottom: 18,
        paddingLeft: 10,
        paddingRight: 10,
        margin: 10,
        alignItems: 'center',
        textAlign: 'center',
        justifyContent: "center",
        backgroundColor: "skyblue",
    },
})
