import React from 'react';
import { View, StyleSheet, Text, Image, TouchableOpacity, ScrollView, Item } from "react-native";

import Carousel from 'react-native-reanimated-carousel';

export default function WarmUpExcercisesScreen() {

    const dataCarousel = [
        {
            img: require('../.././assets/images/warmup/1.png'),
            title: 'Пожимание плечами:',
            text: 'Медленно поднимите плечи как можно выше вверх и снова опустите их.',
        },

    ]

    return(
        <View>
            <Text>
                Разминка подготавливает ваше тело к физическим нагрузкам, чтобы предотвратить травмы. Ваша разминка должна длиться около 5 минут, и в конце разминки Вы должны почувствовать легкую одышку. Разминочные упражнения можно выполнять сидя или стоя. Если Вы выполняете разминку стоя, держитесь за устойчивую поверхность для поддержки, если это необходимо. 
                <Text style={styles.bold}> Повторите каждое движение 2-4 раза.</Text>
            </Text>

            {/* <Carousel
                width={300}
                height={150}
                data={dataCarousel}
                renderItem={({ item }) => (
                    <View>
                        <Image 
                            source={item.img} 
                            />
                        <Text style={styles.bold}>{item.title}</Text>
                        <Text>{item.text}</Text>
                    </View>
                )}
            />; */}
        </View>
    )
}

const styles = StyleSheet.create({
    bold: {
        fontWeight: 'bold',
    },

    btn: {
        borderRadius: 25,
        width: 180,
        paddingTop: 18,
        paddingBottom: 18,
        paddingLeft: 10,
        paddingRight: 10,
        margin: 10,
        alignItems: 'center',
        textAlign: 'center',
        justifyContent: "center",
        backgroundColor: "skyblue",
    },
})
