import { useState, useEffect, useMemo, React, } from 'react';
import AppLoading from 'expo-app-loading';
import { View,} from "react-native";

import  * as Font from 'expo-font';
import { ActivityIndicator } from "react-native-web";

import DrawerNavigator from './src/navigation/DrawerNavigator';
import { AuthContext } from './src/components/context';

const fonts = () => Font.loadAsync({
    'rs-bold': require('./src/assets/fonts/RobotoSlab/RobotoSlab-Bold.ttf'),
    'rs-light': require('./src/assets/fonts/RobotoSlab/RobotoSlab-Light.ttf')
})

export default function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [userToken, setUserToken] = useState(null);

  const authContext = useMemo(() => ({
    signIn: () => {
      setUserToken('asdf');
      setIsLoading(false);
    },
    signOut: () => {
      setUserToken(null);
      setIsLoading(false);
    },
    signUp: () => {
      setUserToken('asdf');
      setIsLoading(false);
    }
  }));

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 1000);
  }, []);

  if (isLoading) {
    return(
      <View style={{flex: 1, justifyContent: 'center', alignItems: 'center',}}>
        <ActivityIndicator size="large"/>
      </View>
    )
  }

  const [font, setFont] = useState(false);

  if(font) {
    return (
      <AuthContext.Provider value={authContext}>
              <DrawerNavigator />
      </AuthContext.Provider>
    );
  } else {
    return (
      <AppLoading startAsync={fonts} 
        onFinish={() => setFont(true)}
        onError={console.warn} />
    );
  }
}
