import { useState, useEffect, React, BackHandler, Alert } from 'react';
import AppLoading from 'expo-app-loading';
import  * as Font from 'expo-font';

import DrawerNavigator from './src/navigation/DrawerNavigator';

const fonts = () => Font.loadAsync({
    'rs-bold': require('./src/assets/fonts/RobotoSlab/RobotoSlab-Bold.ttf'),
    'rs-light': require('./src/assets/fonts/RobotoSlab/RobotoSlab-Light.ttf')
})

export default function App() {
  const [font, setFont] = useState(false);

  const backAction = () => {
    Alert.alert("Вы уверены, что хотите выйти из приложения?", [
      {
        text: "Нет",
        onPress: () => null,
        style: "cancel"
      },
      { text: "Да", onPress: () => BackHandler.exitApp() }
    ]);
    return true;
  };

  useEffect(() => {
    BackHandler.addEventListener("hardwareBackPress", backAction);
      return () =>
        BackHandler.removeEventListener("hardwareBackPress", backAction);
  }, []);

  if(font) {
    return (
      <DrawerNavigator />

    );
  } else {
    return (
      <AppLoading startAsync={fonts} 
        onFinish={() => setFont(true)}
        onError={console.warn} />
    );
  }
}
