import { React, useState, useRef } from 'react';
import { View, Text, StyleSheet, FlatList, Animated, ScrollView, useWindowDimensions, TouchableOpacity, Image } from 'react-native';
import { getStatusBarHeight } from 'react-native-status-bar-height';
import RegistrationScreen from './RegistrationScreen';
import Paginator from '../components/Paginator';

export default function WelcomeScreen({ navigation }) {

  const window = useWindowDimensions();

  const carouselData = [
    {
      id: '1',
      title: (
        <Text>СЛЕДИ ЗА АКТУАЛЬНЫМИ НОВОСТЯМИ О <Text style={{ color: '#FF512F' }}>COVID-19</Text></Text>
      ),
      text: '',
      image: require('../assets/images/wave1.png'),
      textStyle: {

      }
    },
    {
      id: '2',
      title: (
        <Text>ЗАПОЛНЯЙ <Text style={{ color: '#FF9900' }}>ДНЕВНИК</Text> НАБЛЮДЕНИЙ И ПОЛУЧАЙ СОВЕТЫ ОТ ДЕЙСТВУЮЩИХ ВРАЧЕЙ</Text>
      ),
      text: '',
      image: require('../assets/images/wave2.png'),
      textStyle: {

      }
    },
    {
      id: '3',
      title: (
        <Text>СИНХРОНИЗИРУЙ{'\n'}ПРИЛОЖЕНИЕ С <Text style={{ color: '#FF9900' }}>УМНЫМИ{'\n'}ЧАСАМИ</Text></Text>
      ),
      text: '',
      image: require('../assets/images/wave3.png'),
      textStyle: {

      }
    }
  ]

    const [currentIndex, setCurrentIndex] =useState(0);
    const scrollX = useRef(new Animated.Value(0)).current;
    const slideRef = useRef(null);
    const viewableItemsChanged = useRef(({ viewableItems }) => {
        setCurrentIndex(viewableItems[0].index);
    }).current;

    const viewConfig =useRef({ viewAreaCoveragePercentThreshold: 50 }).current;

    return (
        <View style={[styles.container, { marginTop: getStatusBarHeight() }]}>
          <View style={{ height: window.height*0.2 }}>

          </View>
            <Image style={styles.logo} source={require('../assets/images/logo2.png')} />

            <View style={{ height: window.height*0.6 }}>
                <FlatList
                    data={carouselData}
                    renderItem={({ item }) => (
                      <View style={[styles.container, { width: window.width }]}>
                        <View>
                            <ScrollView>
                                <Text style={styles.title}>{item.title}</Text>
                            </ScrollView>
                        </View>

                        <Image source={item.image}
                            style={styles.waves}
                            resizeMode="contain"
                          />
                      </View>
                    )}
                    horizontal
                    showsHorizontalScrollIndicator={false}
                    pagingEnabled
                    bounces={false}
                    keyExtractor={(item) => item.id}
                    onScroll={Animated.event([{ nativeEvent: { contentOffset: { x: scrollX } } }],
                        { useNativeDriver: false, }
                        )}
                    onViewableItemsChanged={viewableItemsChanged}
                    viewabilityConfig={viewConfig}
                    ref={slideRef}
                    />
            </View>
            <Paginator style={{ height: window.height*0.05 }} data={carouselData} scrollX={scrollX}/>

            <View style={{ height: window.height *0.15 }}>
              <TouchableOpacity onPress={() => { navigation.navigate('SignIn') }}>
                <Text style={{ color: 'black' }}>Войти</Text>
              </TouchableOpacity>
              <Text>или</Text>
              <TouchableOpacity onPress={() => { navigation.navigate('SignUp') }}>
                <Text style={{ color: 'black' }}>Регистрация</Text>
              </TouchableOpacity>
            </View>
        </View>
    )
};

const styles = StyleSheet.create({
    container: {
        height: '80%',
        justifyContent: 'center',
        alignItems: 'center',
    },
    title: {
      width: 235,
      left: 40,
      fontWeight: '700',
      fontSize: 14,
    },
    waves: {
      position: 'absolute',
      zIndex: -1,
      alignItems: 'flex-end',
    },
    logo: {
      height: '20%',
      justifyContent: 'center',
      marginBottom: 10,
      width: '100%',
      height: 'auto',
    },
})
